import styled from "styled-components";

export const CardContainer = styled.div`
    display: flex;
    flex-direction: column;
    background: #95dada;
    width: 300px;

    &:hover {
        transform: scale(1.05);
        transition: 0.3s ease-in-out;
    }
`

export const Image = styled.img`
    height: 180px;
    width: 180px;
    display: inline-block;
    margin: 0 auto;
`
