import styled from "styled-components";

export const CardListContainer = styled.div`
    width: 85vw;
    margin: 0 auto;
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(295px, 1fr));
    grid-gap: 2em;


    @media screen and (max-width: 731) {
        display: grid;
        grid-template-columns: 1fr;

    }

`
